$:/plugins/Gk0Wk/TiddlySeq/style/Button.text:
.tc-tiddler-body button:not(.tc-remove-tag-button),
.tc-tab-content button:not(.tc-install-plugin):not(.tc-remove-tag-button),
.tc-edit-tags button:not(.tc-remove-tag-button),
.tc-editor-toolbar button,
.tc-edit-field-add button {
  background: transparent;
  border: none;
  border-radius: 3px;
  padding: 4px 8px;
  color: <<colour foreground>>;
  fill: <<colour foreground>>;
  transition: all {{$:/config/AnimationDuration}}ms;
}

.tc-tiddler-frame .tc-tab-buttons:not(.tc-sidebar-tabs-main):not(.tc-vertical) button {
  background: transparent;
  border: none;
  border-radius: 3px 3px 0 0;
  padding: 4px 8px;
  transition: all {{$:/config/AnimationDuration}}ms;
}

.tc-tiddler-body button:not(.tc-remove-tag-button):hover,
.tc-editor-toolbar button:hover,
.tc-tab-content button:not(.tc-install-plugin):not(.tc-remove-tag-button):hover,
.tc-edit-field-add button:hover,
.tc-edit-tags button:not(.tc-remove-tag-button):hover,
.tc-tiddler-frame .tc-tab-buttons:not(.tc-sidebar-tabs-main) button:hover {
  background: <<colour page-background>>;
  color: <<colour foreground>>;
  fill: <<colour foreground>>;
  filter: contrast(85%);
}

.tc-edit-field-add button.tc-tab-selected,
.tc-edit-tags button.tc-tab-selected,
.tc-tiddler-body button.tc-tab-selected,
.tc-tiddler-frame .tc-tab-buttons:not(.tc-sidebar-tabs-main) button.tc-tab-selected {
  background: <<colour page-background>>;
  color: <<colour foreground>>;
  fill: <<colour foreground>>;
  filter: contrast(70%);
}

.tc-vertical > button {
  padding-top: 10px;
  padding-bottom: 10px;
}

.tc-tag-label > button {
  padding: 0 0 0 4px;
  color: <<colour tag-foreground>>;
  fill: <<colour tag-foreground>>;
}

.tc-tag-label > button:hover {
  background: transparent;
}

$:/plugins/Gk0Wk/TiddlySeq/style/OpenedT.text:
section.story-backdrop {
  top: auto !important;
  bottom: 0;
}

section.story-backdrop button {
  transition: all {{$:/config/AnimationDuration}}ms;
  border: 0 !important;
}

section.story-backdrop > p, section.story-backdrop > p > div.tc-tab-buttons {
  background: rgba(40, 40, 40, 0.3) !important;
}

section.story-backdrop button.tc-tab:not(.tc-tab-selected):hover {
  background-color: <<colour dropdown-tab-background-selected>>;
  filter: contrast(120%);
  border-bottom: 0 !important;
}

$:/plugins/Gk0Wk/TiddlySeq/style/Other.text:
.tc-sidebar-tabs-main > .tc-tab-divider {
  display: none;
}

body.tc-dirty span.tc-dirty-indicator, body.tc-dirty span.tc-dirty-indicator svg {
  color: #FF453A !important;
  fill: #FF453A !important;
}

.tc-drop-down,
.tc-block-dropdown {
  background: <<colour background>>EE;
  filter: contrast(115%);
  opacity: 0.98;
  border: none !important;
  border-radius: 5px;
  margin: 0 !important;
  padding: 0 !important;
  <<box-shadow "#00000070 0 2px 6px">>
  max-height: 70vh;
  overflow-y: auto;
  overflow-x: hidden;
  backdrop-filter: blur(5px);
}

.tc-drop-down a,
.tc-drop-down button {
  padding: 10px 14px !important;
  color: <<colour foreground>> !important;
}

.tc-drop-down a:hover,
.tc-drop-down button:hover,
.tc-drop-down button:active,
.tc-drop-down .tc-file-input-wrapper:hover button {
  color: <<colour background>> !important;
  background: <<colour primary>> !important;
}

.tc-drop-down button svg {
  fill: <<colour foreground>> !important;
}

.tc-drop-down button:hover svg,
.tc-drop-down button:active svg,
.tc-drop-down .tc-file-input-wrapper:hover button svg {
  fill: <<colour background>> !important;
}

.tc-sidebar-tabs-main {
  border: none !important;
}

.tc-tiddler-controls > .tc-popup > .tc-drop-down {
  font-size: 0.5em !important;
}

.tc-tab-content select,
.tc-tiddler-body select {
  border: none;
  background-color: transparent !important;
  width: auto !important;
  height: auto !important;
  padding: 0 !important;
  color: inherit !important;
  font-size: inherit !important;
  border-bottom: 1px solid <<colour very-muted-foreground>>;
  transition: all {{$:/config/AnimationDuration}}ms;
}

.tc-tab-content select:hover,
.tc-tiddler-body select:hover {
  background-color: transparent !important;
  padding: 0 !important;
  border-bottom: 1px solid <<colour muted-foreground>>;
}

.tc-tab-content select option,
.tc-tab-content select optgroup,
.tc-tiddler-body select option,
.tc-tiddler-body select optgroup {
  background: <<colour background>>;
  color: <<colour foreground>>;
  fill: <<colour foreground>>;
}

.tc-tab-content input,
.tc-edit-fields input,
.tc-tiddler-body input,
.tc-menubar-item input {
  border: none;
  background-color: transparent;
  border-bottom: 1px solid <<colour very-muted-foreground>>;
  transition: all {{$:/config/AnimationDuration}}ms;
}

.tc-tab-content input:hover,
.tc-edit-fields input:hover,
.tc-tiddler-body input:hover,
.tc-menubar-item input:hover {
  border-bottom: 1px solid <<colour muted-foreground>>;
}

.tc-tab-content input:focus,
.tc-edit-fields input:focus,
.tc-tiddler-body input:focus,
.tc-menubar-item input:focus {
  outline: none;
  border-bottom: 1px solid <<colour foreground>>;
}

.tc-tab-buttons {
  border-bottom: <<colour table-border>> 1px solid;
}

.tc-tab-content a.tc-tiddlylink:not(.tc-plugin-info),
.tc-tiddler-body a.tc-tiddlylink:not(.tc-plugin-info) {
  opacity: 0.9;
  transition: all {{$:/config/AnimationDuration}}ms;
  text-decoration: none;
  filter: contrast(70%);
}

.tc-tab-content a.tc-tiddlylink:hover,
.tc-tab-content a.tc-tiddlylink:active,
.tc-tiddler-body a.tc-tiddlylink:hover,
.tc-tiddler-body a.tc-tiddlylink:active {
  opacity: 1;
  filter: contrast(100%);
  text-decoration: none;
}

.tc-tab-content a.tc-tiddlylink-external,
.tc-tiddler-body a.tc-tiddlylink-external {
  opacity: 0.9;
  transition: all {{$:/config/AnimationDuration}}ms;
  text-decoration: none;
  filter: contrast(50%);
}

.tc-tab-content a.tc-tiddlylink-external:hover,
.tc-tab-content a.tc-tiddlylink-external:active,
.tc-tiddler-body a.tc-tiddlylink-external:hover,
.tc-tiddler-body a.tc-tiddlylink-external:active {
  opacity: 1;
  filter: contrast(100%);
  text-decoration: none;
}

.tc-tab-content details,
.tc-tiddler-body details {
  background: <<colour dropdown-tab-background>>;
  opacity: 0.95;
}

.tc-tab-content hr,
.tc-tiddler-body hr {
  height: 3px;
  color: inherit;
  border: none;
  background: <<colour foreground>>;
  opacity: 0.4;
}

.tc-tab-content :not(pre)>code,
.tc-tiddler-body :not(pre)>code {
  font-weight: 700;
}

.tc-vertical {
  border-bottom: none !important;
}

$:/plugins/Gk0Wk/TiddlySeq/style/Preview.text:
.tc-popup.tc-preview-tiddler {
  background: <<colour background>>;
  border: none;
  max-height:25%;
  overflow:auto;
  border-radius: 6px;
  <<box-shadow "black 0 0 20px">>
}

$:/plugins/Gk0Wk/TiddlySeq/style/Railroa.text:
svg.railroad-diagram:hover {
    background-color: <<colour page-background>> !important;
}

svg.railroad-diagram path,
svg.railroad-diagram rect {
    stroke: <<colour selection-background>> !important;
}

svg.railroad-diagram path {
    fill: transparent !important;
}

svg.railroad-diagram text {
    fill: <<colour primary>> !important;
}

$:/plugins/Gk0Wk/TiddlySeq/style/Sidebar.text:
.tc-sidebar-scrollable > div {
  width: 100%;
}

.tc-sidebar-scrollable .tc-sidebar-header > .tc-reveal > .tc-reveal > .tc-sidebar-tabs > p > .tc-sidebar-tabs-main > .tc-tab-buttons {
  background: <<colour page-background>>;
  position: sticky;
  top: 0px;
  padding: 14px 10px;
  margin: 0;
  z-index: 1;
}

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarTitle" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-site-title {
  display: none;
}</$reveal>

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarSubtitle" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-site-subtitle {
  display: none;
}</$reveal>

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarPageControl" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-page-controls {
  display: none;
}</$reveal>

<$reveal type="match" state="$:/plugins/Gk0Wk/TiddlySeq/HideSidebarSearch" text="yes">.tc-sidebar-scrollable .tc-sidebar-header .tc-sidebar-search {
  display: none;
}</$reveal>

.tc-sidebar-scrollable .tc-sidebar-header > .tc-reveal > .tc-reveal > .tc-sidebar-tabs > p,
.tc-sidebar-scrollable > div > .tc-sidebar-header > .tc-reveal > div > p {
  margin: 0;
}

.tc-sidebar-scrollable > .tc-sidebar-header,
.tc-sidebar-header > .tc-reveal,
.tc-sidebar-header > .tc-reveal > .tc-reveal,
.tc-sidebar-scrollable .tc-tab-set img {
  width: 100%;
}

.tc-sidebar-scrollable .tc-tab-set a,
.tc-sidebar-scrollable .tc-tab-set p {
  white-space: normal;
}

.tc-sidebar-scrollable .tc-sidebar-header > .tc-reveal > .tc-reveal > .tc-sidebar-tabs > p > .tc-sidebar-tabs-main > .tc-tab-content > .tc-reveal > p {
  margin: 0;
}

@media (min-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
  .tc-sidebar-scrollable > div {
    width: calc(100% - 20px);
  }
  .tc-sidebar-scrollable {
    top: 0;
    bottom: 0;
    right: 0;
    padding: 0 0 {{$:/plugins/Gk0Wk/TiddlySeq/StoryBottomMargin}} 10px;
    background: <<colour page-background>>;
    filter: contrast(90%);
    height: 100vh;
  }
  .tc-sidebar-scrollable .tc-sidebar-header > .tc-reveal > .tc-reveal > .tc-sidebar-tabs > p > .tc-sidebar-tabs-main > .tc-tab-buttons {
    padding: 6px 0;
  }
  .tc-sidebar-scrollable .tc-sidebar-header > .tc-reveal > .tc-reveal > .tc-sidebar-tabs > p > .tc-sidebar-tabs-main > .tc-tab-content {
    margin-top: 0;
  }
}

.tc-tab-buttons.tc-sidebar-tabs-main > button {
  background: transparent;
  border: none;
  font-size: 1.3em;
  opacity: 0.5;
  color: <<colour muted-foreground>>;
  fill: <<colour muted-foreground>>;
  transition: all {{$:/config/AnimationDuration}}ms;
}

.tc-tab-buttons.tc-sidebar-tabs-main > button:hover,
.tc-tab-buttons.tc-sidebar-tabs-main > button.tc-tab-selected {
  font-weight: 600;
  border: none;
  background: transparent;
  color: <<colour foreground>>;
  fill: <<colour foreground>>;
}

.tc-tab-buttons.tc-sidebar-tabs-main > button:hover {
  opacity: 1;
}

.tc-tab-buttons.tc-sidebar-tabs-main > button.tc-tab-selected {
  opacity: 0.8;
}

$:/plugins/Gk0Wk/TiddlySeq/style/Story.text:
html::-webkit-scrollbar {
  display: none;
}

.tc-story-river {
  padding: 0;
  padding-bottom: 0 !important;
}

.tc-tiddler-edit-title {
  line-height: 44px;
}

.tc-tiddler-frame {
  margin-bottom: 0;
}

div.tc-page-container {
  margin-top: 0 !important;
}

<$list filter="[[$:/plugins/tiddlywiki/menubar]is[tiddler][$:/config/Plugins/Disabled/$:/plugins/tiddlywiki/menubar]get[text]else[no]!match[yes]]">
.tc-story-river>.tc-tiddler-frame {
  margin-top: {{$:/plugins/Gk0Wk/TiddlySeq/StorySpacing}};
}
.tc-story-river > .tc-tiddler-frame:first-of-type {
  margin-top: 37.5px;
}
<$list filter="[[$:/themes/tiddlywiki/vanilla/options/stickytitles]get[text]match[yes]]">
.tc-tiddler-title {
  top: 37.5px !important;
  background: <<colour background>><$reveal state="$:/plugins/Gk0Wk/TiddlySeq/BlurTitleBackground" type="match" text="yes">DD</$reveal>;;
<$reveal state="$:/plugins/Gk0Wk/TiddlySeq/BlurTitleBackground" type="match" text="yes">
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
</$reveal>;
}
</$list>
</$list>

@media (min-width: {{$:/themes/tiddlywiki/vanilla/metrics/tiddlerwidth}}) {
  .tc-story-river {
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .tc-tiddler-frame {
    max-width: {{$:/themes/tiddlywiki/vanilla/metrics/tiddlerwidth}};
  }
}

$:/plugins/Gk0Wk/TiddlySeq/style/TiddlyM.text:
.tmap-widget.tmap-advanced-editor .tmap-topbar {
  background: transparent !important;
}

.tmap-widget .tmap-menu-bar {
  color: <<colour foreground>>;
}

.tmap-widget .tmap-topbar {
  background: transparent !important;
  border-bottom: 0 !important;
  color: white !important;
}

.tmap-focus-button .tc-reveal svg {
  fill: white !important;
}

.tmap-widget .tmap-topbar .tmap-view-label {
  visibility: hidden !important;
}

.tmap-has-fullscreen-widget .tmap-widget.tmap-fullscreen {
  background: <<colour page-background>> !important;
}

.tmap-widget div.vis-network .vis-manipulation {
  border-top: 1px solid transparent !important;
  border-color: transparent !important;
  background: transparent !important;
  color: <<colour foreground>> !important;
}

.tmap-unicode-icon {
  color: white !important;
}

.tmap-flash-message.tmap-info,
.tmap-flash-message.tmap-plain,
.tmap-flash-message.tmap-neutral,
.tmap-config-widget {
  background: transparent !important;
  color: white !important;
}

.tc-modal-body table.tmap-key-value-table th,
table.tmap-table tr:nth-child(odd),
.tc-modal-body table.tmap-config-table tr:nth-child(odd) {
  background: rgba(255,255,255,0.1) !important;
  color: white !important;
}

table.tmap-table tr:nth-child(even),
.tc-modal-body table.tmap-config-table tr:nth-child(even) {
  background: transparent !important;
  color: white !important;
}

div.vis-color-picker {
  background: #000000C0 !important;
}

div.tmap-list-separator {
  background: transparent !important;
}

div.vis-color-picker div.vis-initial-color,
div.vis-color-picker div.vis-new-color {
  color: #FFFFFF50 !important;
}

div.vis-color-picker div.vis-arrow {
  display: none !important;
}

div.vis-color-picker div.vis-button {
  background: #000000C0 !important;
}

.tmap-widget .tmap-topbar button svg {
  fill: <<colour foreground>> !important;
}

.tmap-widget .tmap-topbar .tc-drop-down button {
  transition: none;
  border-radius: 0;
}

.tmap-widget .tmap-topbar .tc-drop-down button:hover,
.tmap-widget .tmap-topbar .tc-drop-down button:active {
  background: <<colour primary>> !important;
  color: <<colour foreground>> !important;
  filter: none !important;
}

.tmap-widget .tmap-topbar .tc-drop-down button:hover svg,
.tmap-widget .tmap-topbar .tc-drop-down button:active svg {
  fill: <<colour foreground>> !important;
}

.tmap-tooltip {
  background-color: <<colour background>> !important;
  max-height: 25% !important;
  max-width: 400px !important;
  z-index: 1000 !important;
  border-radius: 6px;
  overflow:auto;
  <<box-shadow "#00000060 0 0 14px">>
  border: none;
  color: <<colour foreground>>;
  fill: <<colour foreground>>;
  padding: 8px;
}

.tmap-widget {
  background: transparent !important;
  border: 0 !important;
  padding: 0 !important;
}
